const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');
require('dotenv').config(); // Load environment variables

// Import Routes
const paymentRoutes = require('./routes/payment');

// Initialize App
const app = express();

// --- MIDDLEWARE ---
// 1. CORS: Allows your frontend (e.g., localhost:5173) to talk to this backend
app.use(cors());

// 2. Body Parser: Essential for reading req.body in POST requests
app.use(express.json());
app.use(express.urlencoded({ extended: true }));


// --- DATABASE CONNECTION (MongoDB) ---
// You can uncomment this part when you have your MONGO_URI in .env
/*
mongoose.connect(process.env.MONGO_URI)
  .then(() => console.log("âœ… MongoDB Connected Successfully"))
  .catch((err) => console.error("âŒ MongoDB Connection Error:", err));
*/


// --- ROUTES ---
// Mount the Payment Route
// This means the URL will be: http://localhost:5000/api/payment/order
app.use('/api/payment', paymentRoutes);

// Simple Test Route
app.get('/', (req, res) => {
    res.send('API is running...');
});


// --- SERVER START ---
const PORT = process.env.PORT || 5000;

app.listen(PORT, () => {
    console.log(`ðŸš€ Server is running on port ${PORT}`);
});
